import React from 'react';
import './cashflow.css'; // This file will contain the necessary CSS
import Footer from './footer';

function updateTotal() {
    // Select all input fields of type text within the section
    const inputs = document.querySelectorAll('.section input[type="text"]');
    
    // Initialize total income
    let totalIncome = 0;

    // Loop through the input fields and sum their values
    inputs.forEach((input, index) => {
        // Skip the last input (total income)
        if (index < inputs.length - 1) {
            const value = parseFloat(input.value) || 0; // Default to 0 if NaN
            totalIncome += value;
        }
    });

    // Update the total income input field (the last input)
    const totalInput = inputs[inputs.length - 1];
    totalInput.value = totalIncome.toFixed(2); // Display total income with two decimal places
}



const CashFlow = () => {
    return (
        <div className="application-form-page">
            {/* Header Section */}
            <header>
                <img src="logo.png" alt="MSU-IIT NMPC Logo" className="logo" />
                <h2 className="landingh2">MSU-IIT National Multi-Purpose Cooperative</h2>
                <nav>
                    <a href="/login" className="nav-link">Login</a>
                    <a href="/register" className="nav-link">Register</a>
                </nav>
            </header>

            {/* Form Container */}
            <div className="form-container">
                <h3 className="title">Cash Flow Statement</h3>
                <form className="cashflow-form">
                    <div className="section">
                        <div className="item">
                            <label>Salaries and Wages:</label>
                            <input type="text" onInput={updateTotal} />
                        </div>
                        <div className="item">
                            <label>Spouse Income:</label>
                            <input type="text" onInput={updateTotal} />
                        </div>
                        <div className="item">
                            <label>Income from Business (Net):</label>
                            <input type="text" onInput={updateTotal} />
                        </div>
                        <div className="item">
                            <label>Income from Financial Investment/Dividend:</label>
                            <input type="text" onInput={updateTotal} />
                        </div>
                        <div className="item">
                            <label>Other Income:</label>
                            <input type="text" onInput={updateTotal} />
                        </div>
                        <div className="item total">
                            <label>Total Income:</label>
                            <input type="text" readOnly />
                        </div>
                    </div>


                    <div className="section">
                        <h3>Expenditures</h3>
                        <div className="item"><label>Food Expenses:</label><input type="text" /></div>
                        <div className="item"><label>House Rentals:</label><input type="text" /></div>
                        <div className="item"><label>Education (Tuition fees, Miscellaneous fees, Books, Supplies, etc.):</label><input type="text" /></div>
                        <div className="item"><label>Medicals and Dental:</label><input type="text" /></div>
                        <div className="item"><label>Clothing:</label><input type="text" /></div>
                        <div className="item"><label>Personal Hygiene:</label><input type="text" /></div>
                        <div className="item"><label>Cooking/Gas:</label><input type="text" /></div>
                        <div className="item"><label>Transportation:</label><input type="text" /></div>
                        <div className="item"><label>Water Bill Payment:</label><input type="text" /></div>
                        <div className="item"><label>Electricity Bill Payment:</label><input type="text" /></div>
                        <div className="item"><label>Telephone/Mobile/Internet Bill Payment:</label><input type="text" /></div>
                        <div className="item"><label>Salary of Helper:</label><input type="text" /></div>
                        <div className="item"><label>Taxes and Licenses:</label><input type="text" /></div>
                        <div className="item"><label>Other Expenses:</label><input type="text" /></div>
                        <div className="item total"><label>Total Expenditures:</label><input type="text" /></div>
                    </div>
                </form>
            </div>

            < Footer />
        </div>
    );
};

export default CashFlow;
